import React from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Repos from "./Repos";
class User extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: {},
      repos: [],
    };
  }
  async componentDidMount() {
    let user = await axios.get(
      `https://api.github.com/users/${this.props.match.params.id}`
    );
    let repos = await axios.get(
      `https://api.github.com/users/${this.props.match.params.id}/repos`
    );
    this.setState({ user: user.data, repos: repos.data });
  }
  render() {
    return (
      <div>
        <Link to="/" className="btn btn-light">
          Back to Home
        </Link>
        Hireable:
        {this.state.user.hireable ? (
          <i className="fas fa-check text-success"></i>
        ) : (
          <i className="fas fa-times-circle text-danger"></i>
        )}
        <div className="card grid-2">
          <div className="all-center">
            <img
              src={this.state.user.avatar_url}
              className="round-img"
              alt={this.state.user.login}
              style={{ width: "200px" }}
            />
            <h2>{this.state.user.login}</h2>
            <p>Location : {this.state.user.location}</p>
          </div>
          <div>
            {this.state.user.bio && (
              <>
                <h3>Bio</h3>
                <p>{this.state.user.bio}</p>
              </>
            )}
            <a
              href={this.state.user.html_url}
              className="btn btn-dark"
              target="_blank"
              rel="noreferrer"
            >
              Go to github profile
            </a>
            <ul>
              <li>
                <strong>Name:</strong>
                {this.state.user.name}
              </li>
              {this.state.user.company && (
                <li>
                  <strong>Work:</strong>
                  {this.state.user.company}
                </li>
              )}
              {this.state.user.blog && (
                <li>
                  <strong>Website:</strong>
                  {this.state.user.blog}
                </li>
              )}
            </ul>
          </div>
        </div>
        <div className="card text-center">
          <div className="badge badge-primary">
            Followers: {this.state.user.followers}
          </div>
          <div className="badge badge-success">
            Following:{this.state.user.following}
          </div>
          <div className="badge badge-primary">
            public_repos:{this.state.user.public_repos}
          </div>
          <div className="badge badge-dark">
            public_gists:{this.state.user.public_gists}
          </div>
        </div>
      </div>
    );
  }
}
export default User;
