// import React, { Component } from "react";
import React, { useState, useEffect } from "react";
import About from "./components/Layout/About";
import NotFound from "./components/Layout/NotFound";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import "./App.css";
import Navbar from "./components/Layout/Navbar";
import AllUsers from "./components/User/AllUsers";
import axios from "axios";
import User from "./components/User/User";
import GithubContext from "./context/GithubContext";
const App = () => {
  const [state, setState] = useState({
    users: [],
    loading: true,
    user: {},
    repos: [],
  });
  const getAllUsers = () => {
    axios.get("https://api.github.com/users").then((res) => {
      setState({ ...state, users: res.data, loading: false });
    });
  };

  const searchUsers = async (query) => {
    setState({ loading: true });
    let res = await axios.get(`https://api.github.com/search/users?q=${query}`);
    setState({ ...state, users: res.data.items, loading: false });
  };
  const getUserInfo = async (username) => {
    setState({ ...state, loading: true });
    let user = await axios.get(`https://api.github.com/users/${username}`);
    let repos = await axios.get(
      `https://api.github.com/users/${username}/repos`
    );
    setState({ ...state, user: user.data, repos: repos.data, loading: false });
  };
  const resetUserInfo = () => {
    setState({ ...state, user: {}, repos: [] });
  };
  return (
    <GithubContext.Provider
      value={{ ...state, getAllUsers, searchUsers, getUserInfo, resetUserInfo }}
    >
      <Router>
        <div className="App" style={{ backgroundColor: "#0D1117" }}>
          <Navbar logo="fab fa-github" appName=" Github App" />
          <div className="container">
            <Switch>
              <Route path="/about">
                <Route exact path="/about">
                  <About teamMember="Us" />
                </Route>
                <Route exact path="/about/mansoor">
                  <About teamMember="Mansoor" />
                </Route>
              </Route>
              <Route
                path="/user/:id"
                render={(props) => (
                  <User
                    user={state.user}
                    repos={state.repos}
                    getUserInfo={getUserInfo}
                    laoding={state.loading}
                    {...props}
                  />
                )}
              />
              <Route exact path="/">
                <AllUsers />
              </Route>
              <Route>
                <NotFound />
              </Route>
            </Switch>
          </div>
        </div>
      </Router>
    </GithubContext.Provider>
  );
};
export default App;
