const App = () => {
  const [state, setState] = useState({
    users: [],
    loading: true,
    user: {},
    repos: [],
  });
  useEffect(() => {
    let res = axios.get("https://api.github.com/users ").then(() => {
      setState({ users: res.data, loading: false });
    });
  }, []);

  const searchUsers = async (query) => {
    setState({ loading: true });
    let res = await axios.get(`https://api.github.com/search/users?q=${query}`);
    setState({ users: res.data.items, loading: false });
  };
  const getUserInfo = async (username) => {
    setState({ loading: true });
    let user = await axios.get(`https://api.github.com/users/${username}`);
    let repos = await axios.get(
      `https://api.github.com/users/${username}/repos`
    );
    setState({ user: user.data, repos: repos.data, loading: false });
  };
  const resetUserInfo = () => {
    setState({ user: {}, repos: [] });
  };
  return (
    <Router>
      <div className="App">
        <Navbar logo="fab fa-github" appName="Github App" />
        <div className="container">
          <Switch>
            <Route path="/about">
              <Route exact path="/about">
                <About teamMember="Us" />
              </Route>
              <Route exact path="/about/mansoor">
                <About teamMember="Mansoor" />
              </Route>
            </Route>
            <Route
              path="/user/:id"
              render={(props) => (
                <User
                  user={state.user}
                  repos={state.repos}
                  getUserInfo={getUserInfo}
                  laoding={state.loading}
                  {...props}
                />
              )}
            />
            <Route exact path="/">
              <Search searchUsers={searchUsers} />
              <Users users={state.users} loading={state.loading} />
            </Route>
            <Route>
              <NotFound />
            </Route>
          </Switch>
        </div>
      </div>
    </Router>
  );
};
export default App;
